import React, { Component } from 'react';
import { Button, Avatar } from 'antd';
import { history } from '../History';
import { logout, getUser, getToken } from '../authentication';
import { Link } from "react-router-dom";

import ChangeProfile from './ChangeProfile';

import './Header.css';

class Header extends Component {

    stste = {
        user: {}
    }

    constructor(props) {
        super(props);
        this.loadingUser();
    }

    loadingUser = () => {
        const userID = getUser().userID
        const requesturl = 'http://localhost:8080/user/getinfo/' + userID
        fetch(requesturl, {
            method: 'GET',
            headers: {
                'token': getToken()
            }
        })
            .then(response => {
                if (response.status === 200) {
                    return response.json();
                } else {
                    console.log('유저 정보 불러오기 에러!')
                    return [];
                }
            }).then(response => this.setState({ user: response }))
    }

    //프로필 사진을 받아오기
    getOnePhoto = (fileName, username) => {
        if (fileName === "" || fileName == null)
            return <Avatar size='large'>{username}</Avatar>
        else
            return <Avatar size='large' src={'http://localhost:8080/photo/download?filename=' + fileName} />
    }



    logOut = () => {
        logout()
        history.push('/login')
    }

    goMain = () => {
        history.push('/main')
    }

    getProfile = () => {
        if (this.state.user == {}) {
            return null;
        } else {
            return (<span>{this.getOnePhoto(this.state.user.profile, this.state.user.username)}{this.state.user.username}</span>);
        }
    }

    render() {
        return (
            <div className='Header'>
                <div className="left-box">
                    <Link to='/main' id='color'>TIMELINE</Link>
                </div>
                <div className="right-box">
                    <ChangeProfile />
                    <Button type="danger" onClick={this.logOut} shape="circle" >q</Button>
                    <Button type="primary" onClick={this.goMain} >Main</Button>
                    {this.getProfile()}
                </div>
            </div>
        );
    }
}

export default Header;
