import React, { Component } from 'react';
import { getUser, getToken } from '../authentication';
import { Modal, Button } from 'antd';

import CommentForm from './CommentForm';
import Comment from './Comment';

class CommentModal extends Component {
  state = {
    visible: false,
    articleID: this.props.articleID,
    comments: []
  };

  constructor(props) {
    super(props)
  }

  //버튼 눌렀을 때, visible state를 바꿔 주어 렌더링 한다음, 댓글을 불러와서 state에 저장
  showModal = () => {
    this.setState({
      visible: true,
    });
    this.loadComments()
  };

  handleOk = e => {
    this.setState({
      visible: false,
    });
  };

  handleCancel = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };


  //댓글 불러오기
  loadComments = () => {
    const requesturl = 'http://localhost:8080/comment/' + this.props.articleID;
    fetch(requesturl, {
      method: 'GET',
      headers: {
        'token': getToken()
      }
    })
    .then(response => {
      if (response.status === 200){
        //성공 시
        return response.json()
      } else {
        //실패
        return null;
      }
    }).then(response => {
      this.setState({ comments: response })
    })
  }

  render() {
    return (
      <div>
        <Button type="default" icon="message" onClick={this.showModal}>댓글</Button>
        <Modal
          title="댓글"
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
          width="750px"
          footer={<Button>OK</Button>}
        >
          <Comment articleID={this.state.articleID} comments={this.state.comments} />
          <CommentForm loadComments={this.loadComments} articleID={this.state.articleID} />
        </Modal>
      </div>
    );
  }
}

export default CommentModal;
