import React, { Component } from 'react';
import { Input } from 'antd';

import './Box.css'


const { Search } = Input;


class Add extends Component {

    state = {
        visibleChemicalName: true,
        visiblePlaceSelect: false,
        chemicalName :"",
        place:"",
        placeList : []
    }

    chemicalNameForm = () => {
        return (
            <div>
                <Search
                    placeholder="약품 이름을 입력해주세요"
                    enterButton="검색"
                    size="large"
                    onSearch={value => {
                        this.setState({visiblePlaceSelect : true})
                        this.getPlaceSuggest(value)
                        }
                    }
                />    
            </div>
        )
    }

    getPlaceSuggest = (name) => {
        console.log(name)
        return (
            <div>
                여기
            </div>
        )
    }



    render() {
        return (
            <div id='box'>
                {this.state.visibleChemicalName && this.chemicalNameForm()}
                {this.state.visiblePlaceSelect && this.getPlaceSuggest()}
            </div>
        );
    }
}

export default Add;
