import React, { Component } from 'react'; 
import { Typography, Icon, Row, Col, Button, Modal, Card, List, Input, AutoComplete} from 'antd'
import { Link } from "react-router-dom"; 

const { Title } = Typography; 

class Apparatus extends Component {

    state = {
    }

    constructor(props){
        super(props);
        const { apparatusId } = this.props.match.params;
        //오늘 날짜 받아오기
        var today = new Date();

        this.state = {
            visible_1: false, //'등록하기' 모달
            visible_2: false, // '예약하기' 모달
            menu: apparatusId,
            apparatusList: [
                {
                    id: 1,
                    name: "apparatus1" 
                }, {
                    id: 2,
                    name: "apparatus2"
                }, {
                    id: 3,
                    name: "apparatus3"
                }, {
                    id: 4,
                    name: "apparatus4"
                }, {
                    id: 5,
                    name: "apparatus5"
                }
            ],
            reservationList:[
                {   
                    apparatusNum: 1,
                    month: 11,
                    date: 23,
                    startTime: 8,
                    endTime: 10,
                    user: '영모'
                },
                {   
                    apparatusNum: 2,
                    month: 11,
                    date: 23,
                    startTime: 8,
                    endTime: 10,
                    user: '주영'
                },
                {   
                    apparatusNum: 2,
                    month: 11,
                    date: 23,
                    startTime: 13,
                    endTime: 16,
                    user: '한빈'
                }
            ],
            todayDate : today
         
            
            
        }
    }

    //'등록하기'버튼에 대한 메소드
    showModal_1 =()=> {
        console.log(this.state.visible_1)
        this.setState({
            visible_1: true
        })
    }
    handleOk_1 =e=> {
        console.log(e);
        this.setState({
            visible_1: false,
        })
    }
    handleCancel_1 =e=> {
        console.log(e);
        this.setState({
            visible_1: false,
        })
    }
    //'예약하기'버튼에 대한 메소드
    showModal_2 =()=> {
        console.log(this.state.visible_1)
        this.setState({
            visible_2: true
        })
    }
    handleOk_2 =e=> {
        console.log(e);
        this.setState({
            visible_2: false,
        })
    }
    handleCancel_2 =e=> {
        console.log(e);
        this.setState({
            visible_2: false,
        })
    }
    
    shouldComponentUpdate(props) {
        return true
    }

    //현재 페이지 내에서 파라미터만 변경되었을 경우
    componentWillReceiveProps(newProps){
        if (this.props.match.params !== newProps.match.params){
            const { apparatusId } = this.props.match.params;
            this.setState({menu : apparatusId})
        }
        //기기별 리스트: 새롭게 선택한 기기를 예약한 사람들 뽑아서 리스트 만들기
        var byAppar = []
        for(var one in this.state.reservationList){
            if( one.apparatusNum === this.state.menu){
                byAppar.push(one);
            }
        }
        this.setState({realReservation : byAppar})

    }


    getUrl = (id) => {
        const url = '/apparatus/' + id;
        return (url)
    }

    goToLeft = () => {
        this.setState({
            todayDate :  this.state.todayDate.setDate(this.state.todayDate.getDate()-1),
            reservationList:[
                {   
                    apparatusNum: 1,
                    month: 11,
                    date: 24,
                    startTime: 8,
                    endTime: 10,
                    user: '혁주'
                },
                {   
                    apparatusNum: 2,
                    month: 11,
                    date: 24,
                    startTime: 8,
                    endTime: 10,
                    user: '은무'
                },
                {   
                    apparatusNum: 3,
                    month: 11,
                    date: 24,
                    startTime: 13,
                    endTime: 16,
                    user: '지수'
                }
            ]
        })
    }
    goToRight = () => {
        this.setState({
            todayDate :  this.state.todayDate.setDate(this.state.todayDate.getDate()+1),
            reservationList:[
                {   
                    apparatusNum: 1,
                    month: 11,
                    date: 25,
                    startTime: 9,
                    endTime: 12,
                    user: '한빈'
                },
                {   
                    apparatusNum: 1,
                    month: 11,
                    date: 25,
                    startTime: 15,
                    endTime: 17,
                    user: '영모'
                },
                {   
                    apparatusNum: 3,
                    month: 11,
                    date: 25,
                    startTime: 13,
                    endTime: 16,
                    user: '지수'
                }
            ]
        })
    }

    makeMonth = () => {
        return (this.state.todayDate.getMonth()+1+"월")
    }
    makeDate = () => {
        return (this.state.todayDate.getDate()+"일")
    }

    render() {
        return (
            <div>
                <br/>
                <center><Title style={{ marginBottom: 35 }}>Apparatus</Title></center>

                <Row >
                    <Col span={8} style={{margin : 10}} >
                        {/* 기기들 목록 */}
                        <List
                            grid={{ gutter: 16, column: 1 }}
                            dataSource={this.state.apparatusList}
                            renderItem={item => (
                                <List.Item>
                                    <Link to={this.getUrl(item.id)}><Button type="primary">{item.name}</Button></Link>
                                </List.Item>
                            )}
                        />
                        <Button onClick={this.showModal_1}>
                            기기 추가
                        </Button>
                        <Modal
                            title="my Apparatus 기기 등록"
                            visible={this.state.visible_1}
                            onOk={this.handleOk_1}
                            onCancel={this.handleCancel_1}
                        >
                            <p>기기이름: <Input placeholder="등록할 기기?" /></p>
                            <p>등록자: 김지수 </p> {/*login user 정보 받으면 넣기*/}
                        </Modal>
                    </Col>
                    <Col span={13} >
                        {/* 기기들 상세 창 */}
                        <Card>
                            
                            <Row span={3}>  
                                <Button type="link" onClick={this.goToLeft}><Icon type="arrow-left" /></Button>
                                    {this.makeMonth()}월 {this.makeDate()}일
                                <Button type="link" onClick={this.goToRight}><Icon type="arrow-right" /></Button>
                            </Row>
                            <Row span={13}>
                                <Col span={3}> 
                                    08 <br/>
                                    09<br/>
                                    10<br/>
                                    11<br/>
                                    12<br/>
                                    13<br/>
                                    14<br/>
                                    15<br/>
                                    16<br/>
                                    17<br/>
                                    18<br/>
                                    19<br/>
                                    20<br/>
                                    21<br/>
                                    22<br/>
                                </Col>
                                <Col span={15}>
                                    {/*예약자 정보*/}
                                </Col>
                            </Row>
                            <Row span={2}>
                                <Col span={19}>
                                    <Button onClick={this.showModal_2}>
                                        예약 하기
                                    </Button>
                                    <Modal
                                        title="my Apparatus 예약 하기"
                                        visible={this.state.visible_2}
                                        onOk={this.handleOk_2}
                                        onCancel={this.handleCancel_2}
                                    >
                                        <p>예약할 기기: <Input placeholder="예약할 기기?" /></p>
                                        <p>예약자: 김지수</p> {/*login user 정보 받으면 넣기*/}
                                        <p>예약할 시간: </p>
                                    </Modal>
                                </Col>
                                <Col span={5}>
                                    <Button type="link">
                                        X
                                    </Button>
                                </Col>
                            </Row>
                        </Card>
                    </Col>
                </Row>


            </div>
        );
    }
}

export default Apparatus;
