import React, { Component } from 'react';
import { Table, Divider, Tag, Modal, Button } from 'antd';
import { serverUrl } from '../setting'

import { getUser } from '../authentication';

import './Box.css'

class List extends Component {


    state = { visible: false };

    state = {

        data : []
    }

    columns = [
        {
            title: '이름',
            dataIndex: 'name',
            key: 'name',
            render: text => (
                <a>
                    <Button type="link" onClick={this.showModal}>
                        {text}
                    </Button>
                    <Modal
                        title="MSDS"
                        visible={this.state.visible}
                        onOk={this.handleOk}
                        onCancel={this.handleCancel}
                    >
                        <p>Some contents...</p>
                        <p>Some contents...</p>
                        <p>Some contents...</p>
                    </Modal>
                </a>),
        },
        {
          title: '보관장소',
          dataIndex: 'place',
          key: 'place',
        },
        {
          title: '생성일',
          dataIndex: 'putDate',
          key: 'putDate',
          render : date => date.year + '/' + date.monthValue + '/' + date.dayOfMonth
        }
    ]
    showModal = () => {
        this.setState({
            visible: true,
        });
    };

    handleOk = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };

    handleCancel = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };


    componentWillMount = () => {
        fetch(serverUrl + '/chemical/list/' + getUser().id, {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' }
        })
            .then(response => {
                const result = response.status;
                if (result === 200) {
                    //성공하였을 경우 
                    console.log(response)
                    response.json().then(response => {
                        console.log(response);
                        this.setState({data : response})
                    })
                } else {
                    //내부 오류
                }
            })
    }

    render() {
        return (
            <div id='box'>
                <Table columns={this.columns} dataSource={this.state.data} />
            </div>
        );
    }
}

export default List;
