import React, { Component } from 'react';
import { Avatar, Button, message } from 'antd';
import { getUser, getToken } from '../authentication';

import './FriendUnit.css';

class FriendUnit extends Component {

    //어디서 왔는지 : this.props.from
    state = {
        relationship : 4
    }

    //프로필 사진을 받아오기
    getOnePhoto = (fileName, username) => {
        if (fileName === "" || fileName == null)
            return <Avatar size='large'>{username}</Avatar>
        else
            return <Avatar size='large' src={'http://localhost:8080/photo/download?filename=' + fileName} />
    }

    getButton = () => {
        const from = this.props.from;
        const requesturl = 'http://localhost:8080/friend/request/check/' + getUser().userID + '/' + this.props.friend.id
        fetch(requesturl, {
            method: 'GET',
            headers: {
                'token': getToken()
            }
        })
            .then(response => {
                if (response.status ===200){
                    return response.json();
                } else {
                    console.log('관계 불러오기 에러!')
                    return [];
                }
            }).then(response => this.setState({relationship : response}))
        console.log('relationship : ' + this.state.relationship )

        if (from === "request") {
            //친구요청 리스트업일 경우
            return (
                <span>
                    <Button type="primary" shape="circle" size="small" onClick={this.acceptRequest}>O</Button>
                    <Button type="danger" shape="circle" size="small" onClick={this.denyRequest}>X</Button>
                </span>
            )
        } else if (from === "suggest") {
            //친구추천 리스트업일 경우
            if (this.state.relationship == 2){
                //이미 친구요청 받은 상태
                return (
                    <span>
                        <Button  size="small" disabled>친구요청받음</Button>
                    </span>
                )
            } else if (this.state.relationship == 3) {
                //이미 친구요청 보낸 상태
                return (
                    <span>
                        <Button  size="small"  disabled>이미신청했음</Button>
                    </span>
                )    
            } else {
                return (
                    <span>
                        <Button  size="small" onClick={this.sendRequest} >친구신청</Button>
                    </span>
                )    
            }
        }
    }

    sendRequest = () => {
        fetch("http://localhost:8080/friend/request/"+getUser().userID+"/"+this.props.friend.id, {
            method: 'POST',
            headers: {
                'token': getToken()
            }
        })
            .then(response => {
                if (response.status == 200) {
                    message.success("친구 요청을 보냈습니다!")
                    this.deleteUnit();
                } else {
                    //오류 처리
                    console.log("error!")
                }
            })

    }

    acceptRequest = () => {
        fetch("http://localhost:8080/friend/request/response/"+1+"/"+this.props.friend.id+"/"+getUser().userID, {
            method: 'GET',
            headers: {
                'token': getToken()
            }
        })
            .then(response => {
                if (response.status == 200) {
                    message.success("지금부터 두분은 친구사입니다!")
                    this.deleteUnit();
                } else {
                    //오류 처리
                    console.log("error!")
                }
            })
    }

    denyRequest = () => {
        fetch("http://localhost:8080/friend/request/response/"+2+"/"+this.props.friend.id+"/"+getUser().userID, {
            method: 'GET',
            headers: {
                'token': getToken()
            }
        })
            .then(response => {
                if (response.status == 200) {
                    message.success("친구 신청을 거절 하였습니다.")
                    this.deleteUnit();
                } else {
                    //오류 처리
                    console.log("error!")
                }
            })
    }

    deleteUnit = () => {
        this.props.deleteArray(this.props.friend)
    }

    render() {
        const profile = this.props.friend.profile;
        const email = this.props.friend.email;
        const userID = this.props.friend.id;
        const username = this.props.friend.username;

        return (
            <div className="request">
                {this.getOnePhoto(profile, username)}
                {username}
                {this.getButton()}
            </div>
        );
    }
}

export default FriendUnit;
