import React, { Component } from 'react';
import {Avatar} from 'antd';
import { getUser, getToken } from '../authentication';

import FriendUnit from './FriendUnit';
import FriendArray from './FriendArray';

class FriendRequest extends Component {

    state = {
        requestList: []
    };

    constructor(props) {
        super(props);

        this.loadingFriendRequests();
    }

    loadingFriendRequests = () => {
        const requesturl = 'http://localhost:8080/friend/request/' + getUser().userID
        fetch(requesturl, {
            method: 'GET',
            headers: {
                'token': getToken()
            }
        })
            .then(response => {
                if (response.status === 200) {
                    return response.json();
                } else {
                    console.log('친구신청 불러오기 에러!')
                    return [];
                }
            }).then(response => {
                this.setState({ requestList: response })
                console.log("친구 요청들")
                console.log(response)
            })

    }

    //삭제한 글 state의 리스트에서 삭제해 주어 다시 렌더링
    deleteArray = (friend) => {
        let lists = this.state.requestList
        lists.splice(lists.indexOf(friend),1);
        this.setState({requestList : lists})
    }

    render() {
        return (
            <div>
                친구 요청
                <FriendArray friends={this.state.requestList} from="request" deleteArray={this.deleteArray}/>
            </div>
        );
    }
}

export default FriendRequest;
