import React, { Component } from 'react';
import { Input } from 'antd';
import { serverUrl } from '../setting'

import './Box.css'


const { Search } = Input;


class Add extends Component {

    state = {
        visibleChemicalName: true,
        visiblePlaceSelect: false,
        chemicalName :"",
        place:"",
        placeList : []
    }

    chemicalNameForm = () => {
        return (
            <div>
                <Search
                    placeholder="약품 이름을 입력해주세요"
                    enterButton="검색"
                    size="large"
                    onSearch={value => {
                        this.setState({visiblePlaceSelect : true})
                        this.getPlaceSuggest(value)
                        }
                    }
                />    
            </div>
        )
    }

    getPlaceSuggest = (name) => {
        console.log(name)

        fetch(serverUrl + '/chemical/request', {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' },
        })
            .then(response => {
                const result = response.status;
                if (result === 200) {
                    //성공하였을 경우 : 리스트에 저장
                    console.log(response)
                    this.setState({
                    })
                } else {
                    //내부 오류
                }
            })


        return (
            <div>
                여기
            </div>
        )
    }



    render() {
        return (
            <div id='box'>
                {this.state.visibleChemicalName && this.chemicalNameForm()}
                {this.state.visiblePlaceSelect && this.getPlaceSuggest()}
            </div>
        );
    }
}

export default Add;
