import React, { Component } from 'react';
import { Avatar, Button } from 'antd';
import { getUser, getToken } from '../authentication';

import './FriendUnit.css';

class FriendUnit extends Component {

    //어디서 왔는지 : this.props.from

    //프로필 사진을 받아오기
    getOnePhoto = (fileName, username) => {
        if (fileName === "" || fileName == null)
            return <Avatar size='large'>{username}</Avatar>
        else
            return <Avatar size='large' src={'http://localhost:8080/photo/download?filename=' + fileName} />
    }

    getButton = () => {
        const from = this.props.from;
        if (from === "request") {
            return (
                <span>
                    <Button size="small">Y</Button>
                    <Button type="dashed" size="small">N</Button>
                </span>
            )
        }
    }

    render() {
        const profile = this.props.request.profile;
        const email = this.props.request.email;
        const userID = this.props.request.id;
        const username = this.props.request.username;

        return (
            <div className="request">
                {this.getOnePhoto(profile, username)}
                {username}
                {this.getButton()}
            </div>
        );
    }
}

export default FriendUnit;
