import React, { Component } from 'react';
import ArticleArray from './ArticleArray';
import { Avatar, Button, message } from 'antd';

import { getUser, getToken } from '../authentication';
import {getFriendButton} from './function';

import './Box.css';

class Home extends Component {
    state = {
        articles: [],
        user: {},
        relationship : 4,
        other : 1
    }

    constructor(props) {
        super(props)

        this.loadingUser();
        this.loadingArticles()
        this.loadingRelationship();
        const { userID } = this.props.match.params;
        this.setState({other : userID})
    }

    shouldComponentUpdate(props) {
        return true
    }

    loadingRelationship = () => {
        const { userID } = this.props.match.params
        const requesturl = 'http://localhost:8080/friend/request/check/' + getUser().userID + '/' + userID
        fetch(requesturl, {
            method: 'GET',
            headers: {
                'token': getToken()
            }
        })
            .then(response => {
                if (response.status ===200){
                    return response.json();
                } else {
                    console.log('관계 불러오기 에러!')
                    return [];
                }
            }).then(response => this.setState({relationship : response}))

    }

    loadingUser = () => {
        const { userID } = this.props.match.params
        const requesturl = 'http://localhost:8080/user/getinfo/' + userID
        fetch(requesturl, {
            method: 'GET',
            headers: {
                'token': getToken()
            }
        })
            .then(response => {
                if (response.status === 200) {
                    return response.json();
                } else {
                    console.log('유저 정보 불러오기 에러!')
                    return [];
                }
            }).then(response => this.setState({ user: response }))
    }


    loadingArticles = () => {
        const { userID } = this.props.match.params
        const requesturl = 'http://localhost:8080/article/home/' + userID
        fetch(requesturl, {
            method: 'GET',
            headers: {
                'token': getToken()
            }
        })
            .then(response => {
                if (response.status === 200) {
                    return response.json();
                } else {
                    console.log('소식 불러오기 에러!')
                    return [];
                }
            }).then(response => this.setState({ articles: response }))
    }

    //삭제한 글 state의 리스트에서 삭제해 주어 다시 렌더링
    deleteArticle = (article) => {
        let articles = this.state.articles
        articles.splice(articles.indexOf(article), 1);
        this.setState({ articles: articles })
    }

    //프로필 사진을 받아오기
    getOnePhoto = (fileName, username) => {
        if (fileName === "" || fileName == null)
            return <Avatar size='large'>{username}</Avatar>
        else
            return <Avatar size='large' src={'http://localhost:8080/photo/download?filename=' + fileName} />
    }

    getButton = () => {
        const { userID } = this.props.match.params
        if (getUser().userID == userID){
            return null;
        } else if (this.state.relationship == 1){
            //이미 친구인 상태
            return (
                <span>
                    <Button type="danger" size="small" ghost>친구끊기</Button>
                </span>
            )
        } else if (this.state.relationship == 2){
            //이미 친구요청 보낸  상태
            return (
                <span>
                    <Button  size="small" disabled>이미신청했음</Button>
                </span>
            )
        } else if (this.state.relationship == 3) {
            //이미 친구요청 받은 상태
            return (
                <span>
                    <Button  size="small"  disabled>친구요청받음</Button>
                </span>
            )    
        } else {
            return (
                <span>
                    <Button type="primary" size="small" onClick={this.sendRequest} ghost>친구신청</Button>
                </span>
            )    
        }
    }

    render() {
        return (
            <div>
                <div id='box'>
                    {this.getOnePhoto(this.state.user.profile, this.state.user.username)}{this.state.user.username}의 소식 모아보기
                    {getFriendButton(getUser().userID, this.state.other, this.state.relationship)}
                </div>
                <ArticleArray articles={this.state.articles} deleteArticle={this.deleteArticle} />
            </div>
        );
    }
}

export default Home;
