import React, { Component } from 'react';
import ArticleArray from './ArticleArray';
import { getUser, getToken } from '../authentication';
import ArticleForm from './ArticleForm';

import './Timeline.css';




class Timeline extends Component {
    state = {
        articles: []
    }

    constructor(props) {
        super(props)

        //소식 목록 로딩
        this.loadingArticles()
    }

    shouldComponentUpdate(props) {
        return true
      }

    //소식 목록 로딩
    loadingArticles = () => {
        const requesturl = 'http://localhost:8080/article/timeline/' + getUser().userID
        fetch(requesturl, {
            method: 'GET',
            headers: {
                'token': getToken()
            }
        })
            .then(response => response.json())
            .then(response => {
                this.setState({ articles: response })
            })
    }

    //새로 입력한 글 소식 리스트의 맨앞에 넣어주어 렌더링
    addArticle = (article) => {
        let articles = this.state.articles
        articles.unshift(article)
        this.setState({articles : articles})
    }

    //삭제한 글 state의 리스트에서 삭제해 주어 다시 렌더링
    deleteArticle = (article) => {
        let articles = this.state.articles
        articles.splice(articles.indexOf(article),1);
        this.setState({articles : articles})
    }

    render() {
        return (
            <div>
                <ArticleForm callBack={this.addArticle} />
                <ArticleArray articles={this.state.articles} callBack={this.deleteArticle} />
            </div>
        );
    }
}

export default Timeline;
