import React, { Component } from 'react';
import { Form, Icon, Input, Button, message, Modal } from 'antd';
import { Link } from "react-router-dom";
import { Typography } from 'antd';
import { getUser, getLab } from '../authentication';
import { history } from '../History';
import { serverUrl } from '../setting';
import { login } from '../authentication';

const { Title } = Typography;

class MyGroupRegister extends Component {

  state = { visible: false };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        //요청 양식은 이미 values에 동일하게 맞춰져 있는 상태이므로
        //따로 가공해줄 필요 없이 바로 http 요청을 보낸다
        console.log(values)
        fetch(serverUrl + '/lab/' + getLab().id + '/' + getUser().id, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(values)
        })
          .then(response => {
            const result = response.status;
            if (result === 200) {
              //로그인이 성공하였을 경우
              response.json().then(response => {
                //로컬스토리지에 토큰 및 로그인 정보 저장
                login(response)
                console.log(response)
                message.success(response.user.name + '그룹 가입에 성공하였습니다!');
                this.props.enrollLab(3);
                history.push("/main")
              })
            } else if (result === 401) {
              //비밀번호 불일치 혹은 이메일이 등록되지 않았음
              message.error('그룹 가입 신청에 실패하였습니다. 그룹 이름 혹은 그룹 비밀번호를 다시 확인해 주세요.');
              console.log("fail!")
            } else {
              //내부 오류
            }
          })
      }
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log(values)
        const registInformation = {
          name: values.groupName,
          password: values.password,
        }
        //http요청
        fetch(serverUrl + '/lab/' + getLab().id + '/' + getUser().id, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ "password": registInformation.password })
        }).then(response => {
          if (response.status === 200) {
            //가입이 성공적으로 수행 되었을 경우
            message.success('그룹의 맴버가 되신 것을 환영합니다!');
          } else {
            //요청 오류 발생
            message.warning('올바른 패스워드가 아닙니다!');
          }
        })
      } else {
      }
    });
  };

  //비밀번호 검증 과정인데 따로 규칙 지정 안했으므로 항상 true
  validateToNextPassword = (rule, value, callback) => {
    const { form } = this.props;
    form.validateFields(['confirm'], { force: true });
    callback();
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    return (
      <Form onSubmit={this.handleSubmit} className="form">

        {/* 이름 */}
        <Form.Item>
          {getFieldDecorator('groupName', {
            rules: [{ required: true, message: '그룹 이름을 입력해 주세요!' }],
          })(
            <Input
              prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
              placeholder="Group Name"
            />,
          )}
        </Form.Item>

        {/* 비밀번호 */}
        <Form.Item hasFeedback>
          {getFieldDecorator('password', {
            rules: [
              {
                required: true, message: '비밀번호를 입력 해 주세요!',
              },
              {
                validator: this.validateToNextPassword,
              },
            ],
          })(<Input.Password
            prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
            placeholder="Group Password" />)}
        </Form.Item>

        {/* 가입 버튼 */}
        <Form.Item>
          <Button type="primary" htmlType="submit" className="button">
            가입 신청
          </Button>
        </Form.Item>
      </Form>
    );
  }
}

export default Form.create()(MyGroupRegister);
