import React, { Component } from 'react';
import { Form, Icon, Input, Button, Upload, Modal } from 'antd';

function getBase64(file) {
    return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result);
        reader.onerror = error => reject(error);
    });
}  

const { TextArea } = Input;

class ArticleForm extends Component {

    state = {
        previewVisible: false,
        previewImage: '',
        fileList: [

        ],
    };

    handleCancel = () => this.setState({ previewVisible: false });

    handlePreview = async file => {
        if (!file.url && !file.preview) {
            file.preview = await getBase64(file.originFileObj);
        }

        this.setState({
            previewImage: file.url || file.preview,
            previewVisible: true,
        });
    };

    handleChange = ({ fileList }) => this.setState({ fileList });

    //여기위까지가 사진 폼 

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                //userID 임시 설정
                let article = {
                    'userID' : 1,
                    'content' : values.content
                }
                console.log('Received values of form: ', values);
                console.log(this.state.fileList) //여기서 각 원소의 originFileObj를 보내주면 됩니다
                fetch('http://localhost:8080/article/insert', {
                    method:'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body : JSON.stringify(article)
                }).then(response => response.json())
                .then(rsp => {
                    console.log(rsp)
                    const userID = 1 //임시로 넣어준 유저아이디
                    const articleID = rsp.data.articleID
                    const data = new FormData();
                    this.state.fileList.map((file,i) => {
                        data.append('file'+i, file.originFileObj)
                        console.log('seperate file')
                        console.log(file.originFileObj)
                        console.log('data 중간체크 ')
                        console.log(data)
                    })
                    console.log('stringify data')
                    console.log(JSON.stringify(data))
                    fetch('http://localhost:8080/article/insertphoto/'+userID+'/'+articleID, {
                        method: 'POST',
                        headers: { 'Content-Type': 'application/json' },
                        body : {files :JSON.stringify(data)}
                    })
                })

            }
        });
    };

    render() {

        //여기부터 사진폼

        const { previewVisible, previewImage, fileList } = this.state;
        const uploadButton = (
          <div>
            <Icon type="plus" />
            <div className="ant-upload-text">Upload</div>
          </div>
        );

        //여기까지가 사진 폼 

        const { getFieldDecorator } = this.props.form;

        return (
            <Form onSubmit={this.handleSubmit} className="article-form">
                <Form.Item>
                    {getFieldDecorator('content', {
                        rules: [{ required: true, message: 'Please input your story!' }],
                    })(
                        <TextArea
                            placeholder="오늘 무슨 일이 일어났나요?"
                            autosize={{ minRows: 5, maxRows: 6 }}
                        />,
                    )}
                </Form.Item>
                <Form.Item>
                    {getFieldDecorator('photo', {
                        rules: [{ required: false }],
                    })(
                        <div className="clearfix">
                            <Upload
                                name="file"
                                action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                listType="picture-card"
                                fileList={fileList}
                                onPreview={this.handlePreview}
                                onChange={this.handleChange}
                            >
                                {fileList.length >= 5 ? null : uploadButton}
                            </Upload>
                            <Modal visible={previewVisible} footer={null} onCancel={this.handleCancel}>
                                <img alt="example" style={{ width: '100%' }} src={previewImage} />
                            </Modal>
                        </div>
                    )}
                </Form.Item>
                <Form.Item>
                    <Button type="primary" htmlType="submit" className="article-form-button">
                        Upload
                    </Button>
                </Form.Item>
            </Form>
        );
    }
}

export default Form.create()(ArticleForm);
