    state = {

        data : []
    }

    columns = [
        {
          title: '이름',
          dataIndex: 'name',
          key: 'name',
          render: text => <a>{text}</a>,
        },
        {
          title: '보관장소',
          dataIndex: 'place',
          key: 'place',
        },
        {
          title: '생성일',
          dataIndex: 'putDate',
          key: 'putDate',
        }
      ]
