import React, { Component } from 'react';
import { getUser, getToken } from '../authentication';
import { List, Avatar } from 'antd'

import './Comment.css'
class Comment extends Component {

    articleID = this.props.articleID;



    render() {
        // Get a reference to the div you want to auto-scroll.

        var someElement = document.querySelector('.list');
        // Create an observer and pass it a callback.
        var observer = new MutationObserver(scrollToBottom);
        // Tell it to look for new children that will change the height.
        var config = { childList: true };

        function scrollToBottom() {
            someElement.scrollTop = someElement.scrollHeight
        }
        return (
            <div className='list'>
                {observer.observe(someElement, config)}
                <List
                    itemLayout="horizontal"
                    dataSource={this.props.comments}
                    renderItem={item => (
                        <List.Item>
                            <List.Item.Meta
                                avatar={<Avatar src={"http://localhost:8080/photo/download?filename=" + item.profile} />}
                                title={item.username}
                                description={item.content}
                            />
                        </List.Item>
                    )}
                />
            </div>
        );
    }
}

export default Comment;
