import React, { Component } from 'react';
import { getUser, getToken } from '../authentication';
import { List, Avatar } from 'antd'

import './Comment.css'
class Comment extends Component {

    articleID = this.props.articleID;

    constructor(props) {
        super(props)
        this.scrollToBottom();

    }

    componentDidMount() {
        this.scrollToBottom();
    }

    componentDidUpdate() {
        this.scrollToBottom();
    }

    scrollToBottom() {
        this.el.scrollIntoView({ behavior: 'smooth' });
    }

    render() {
        return (
            <div className='list' ref={el => { this.el = el; }}>
                <List
                    itemLayout="horizontal"
                    dataSource={this.props.comments}
                    renderItem={item => (
                        <List.Item>
                            <List.Item.Meta
                                avatar={<Avatar src={"http://localhost:8080/photo/download?filename=" + item.profile} />}
                                title={item.username}
                                description={item.content}
                            />
                        </List.Item>
                    )}
                />
            </div>
        );
    }
}

export default Comment;
