import React, { Component } from 'react';
import { getUser, getToken } from '../authentication';
import { List, Avatar, message, Popconfirm} from 'antd'

import './Comment.css'
class Comment extends Component {

    //스크롤을 댓글 제일 아래로 유지하기 위해 (최신 댓글이 제일 아래 위치하므로)
    scrollToBottom() {
        const scrollHeight = this.messageList.scrollHeight;
        const height = this.messageList.clientHeight;
        const maxScrollTop = scrollHeight - height;
        this.messageList.scrollTop = maxScrollTop > 0 ? maxScrollTop : 0;
    }
    componentDidUpdate() {
        this.scrollToBottom();
    }
    componentDidMount() {
        this.scrollToBottom();
    }

    //날짜를 원하는 형식에 맞춰서 출력
    getDate = (date) => {
        return "" + date.date.year + "/" + date.date.month + "/" + date.date.day + " " + date.time.hour + ":" + date.time.minute
    }


    //프로필 사진을 얻기 위해
    getOnePhoto = (fileName, username) => {
        if (fileName === "" || fileName == null)
            return <Avatar size='large'>{username}</Avatar>
        else
            return <Avatar size='large' src={'http://localhost:8080/photo/download?filename=' + fileName} />
    }


    //댓글 삭제
    confirm = (item) => {
        console.log('@')
        console.log(item)
        /*
        fetch("http://localhost:8080/comment/" + commentID, {
            method: 'DELETE',
            headers: {
                'token': getToken()
            }
        })
            .then(response => {
                if (response.status == 200) {
                    //댓글 삭제 처리
                    this.props.deleteComment(comment);
                    message.success('댓글 삭제가 완료 되었습니다!');
                } else {
                    //오류 처리
                }
            })
            */
    }
    cancel = (e) => {
        console.log(e);
    }

    //댓글 삭제 버튼
    deleteButton = (userID, commentUserID, commentID, comment) => {
        if (userID === commentUserID) {
            return (
                <Popconfirm
                    title="이 댓글을 삭제하시겠습니까?"
                    onConfirm={this.confirm}
                    onCancel={this.cancel}
                    okText="Yes"
                    cancelText="No"
                >
                    <a href="#">X</a>
                </Popconfirm>
            );
        } else {
            return null;
        }
    }

    render() {
        return (
            <div className='list'
                ref={(div) => {
                    this.messageList = div;
                }}
            >
                <List
                    itemLayout="horizontal"
                    dataSource={this.props.comments}
                    renderItem={item => (
                        <List.Item>
                            <List.Item.Meta
                                avatar={this.getOnePhoto(item.profile, item.username)}
                                title={
                                    <span>
                                        <b>{item.username}</b>
                                        <span id="date">
                                            {this.getDate(item.createdtime)}
                                        </span>
                                        <span>
                                            {this.deleteButton(getUser().userID, item.userID, item.commentID, item)}
                                        </span>
                                    </span>
                                }
                                description={item.content}
                            />
                        </List.Item>
                    )}
                />
            </div>
        );
    }
}

export default Comment;
