import React, { Component } from 'react';
import { Modal, Button } from 'antd';

import ChemicalInfo from './ChemicalInfo';
import StockInfo from './StockInfo';

//화학 약품 하나에 대한 거 (my Lab 화면 중에)
class Chemical extends Component {

    static defaultProps = {
        information: [
            {
                stockId: 1, //임시 id
                inventory: "냉장고", //임시 장소    
                putDate: "2017/10/9",
                expireDate: "2019/10/9",
                volume: 100,
                remainingVolume: 50
            },
            {}

        ],

    }


    state = {
        visible: false
    }

    constructor(props) {
        super(props);
    }

    showModal = () => {
        this.setState({
            visible: true,
        });
    };

    handleOk = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };

    handleCancel = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };


    handleRemove = () => {
        // 삭제 버튼이 클릭되면 onRemove 에 id 넣어서 호출
        const { information, onRemove } = this.props;
        onRemove(information.stockId);
    }


    render() {
        const { data, onRemove } = this.props;
        const {
            handleOk,
            handleCancle,
            handleRemove
        } = this;
        const information = data.map(
            information => (
                <StockInfo
                    key={information.stockid}
                    information={information}
                    onRemove={onRemove}
                />)
        );
        return (
            <span>
                <center>
                    {/* 버튼 */}
                    <Button Button shape="circle" icon="fire" onClick={this.showModal} style={{ fontSize: '25px' }} size="large" />
                    {/* 뜨는 창 */}
                    <Modal
                        title="약품 정보"
                        visible={this.state.visible}
                        onOk={this.handleOk}
                        onCancel={this.handleCancel}
                    >
                        {/* 정보 출력  */}
                        <ChemicalInfo chemical={this.props.chemical} />
                        <StockInfo chemical={this.props.chemical} />
                        여기에 사용량 업데이트 <br />
                        장소수정 <br />
                        <button onClick={this.handleRemove}>폐기하기</button>
                    </Modal>
                </center>
                <center>
                    {this.props.chemical.nickname}
                </center>
            </span>
        );
    }
}

export default Chemical;
