import React, { Component } from 'react';
import {Avatar} from 'antd';
import { getUser, getToken } from '../authentication';

class FriendRequestUnit extends Component {

    state = {
    };

    constructor(props) {
        super(props);
        this.setState({
            profile : this.props.request.profile,
            email : this.props.request.email,
            userID : this.props.request.id,
            username : this.props.request.username
        })
        console.log(this.props.request)
    }

    //프로필 사진을 받아오기
    getOnePhoto = (fileName, username) => {
        if (fileName === "" || fileName == null)
            return <Avatar size='large'>{username}</Avatar>
        else
            return <Avatar size='large' src={'http://localhost:8080/photo/download?filename=' + fileName} />
    }


    render() {
        return (
            <div>
                {this.getOnePhoto(this.state.profile, this.state.username)}
                {this.state.username}
            </div>
        );
    }
}

export default FriendRequestUnit;
