import React, { Component } from 'react';
import { Modal, Button } from 'antd';

import ChemicalInfo from './ChemicalInfo';
import StockInfo from './StockInfo';

//화학 약품 하나에 대한 거 (my Lab 화면 중에)
class Chemical extends Component {

 


    state = {
        visible: false
    }

    constructor(props) {
        super(props);
    }

    showModal = () => {
        this.setState({
            visible: true,
        });
    };

    handleOk = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };

    handleCancel = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };


    handleRemove = () => {
        // 삭제 버튼이 클릭되면 onRemove 에 id 넣어서 호출
        alert("폐기하기를 누르셨습니다.");
        const { chemical } = this.state;
        const index = chemical.findIndex()
    }
    handleChange = () => {
        alert("장소 수정을 눌렀습니다.");
    }

    handleUpdate = () => {
        alert("사용량 업데이트를 눌렀습니다.");
    }

    render() {
        const { data, onRemove } = this.props;
        const {
            handleOk,
            handleCancle,
            handleRemove
        } = this;

        return (
            <span>
                <center>
                    {/* 버튼 */}
                    <Button Button shape="circle" icon="fire" onClick={this.showModal} style={{ fontSize: '25px' }} size="large" />
                    {/* 뜨는 창 */}
                    <Modal
                        title="약품 정보"
                        visible={this.state.visible}
                        onOk={this.handleOk}
                        onCancel={this.handleCancel}
                    >
                        {/* 정보 출력  */}
                        <ChemicalInfo chemical={this.props.chemical} />
                        <StockInfo chemical={this.props.chemical} />
                        <input  placeholder="사용량을 적으세요"></input> 
                        <button  onClick = {this.handleUpdate}>사용량 업데이트</button><br/>
                        <button onClick = {this.handleChange}>장소 수정</button><br/>
                        <button  onClick = {this.handleRemove}>폐기 하기</button>
                    </Modal>
                </center>
                <center>
                    {this.props.chemical.nickname}
                </center>
            </span>
        );
    }
}

export default Chemical;
