import React, { Component } from 'react'; 
import { Typography, Icon, Row, Col, Button, Modal, Card, List} from 'antd'
import { Link } from "react-router-dom"; 

const { Title } = Typography; 

class Apparatus extends Component {

    state = {
        visible_1: false,
        visible_2: false
    }

    constructor(props){
        super(props);
        const { apparatusId } = this.props.match.params;
        this.state = {
            menu : apparatusId,
            apparatusList: [
                {
                    id: 1,
                    name: "apparatus1" 
                }, {
                    id: 2,
                    name: "apparatus2"
                }, {
                    id: 3,
                    name: "apparatus3"
                }, {
                    id: 4,
                    name: "apparatus4"
                }, {
                    id: 5,
                    name: "apparatus5"
                }
            ],
            reservationList:[
                {   
                    apparatusNum: 1,
                    month: 11,
                    date: 23,
                    startTime: 8,
                    endTime: 10,
                    user: '영모'
                },
                {   
                    apparatusNum: 2,
                    month: 11,
                    date: 23,
                    startTime: 8,
                    endTime: 10,
                    user: '주영'
                },
                {   
                    apparatusNum: 2,
                    month: 11,
                    date: 23,
                    startTime: 13,
                    endTime: 16,
                    user: '한빈'
                }
            ]
        }
    }

    showModal_1 =()=> {
        this.setState({
            visible_1: true,
        })
    }

    handleOk_1 =e=> {
        console.log(e);
        this.setState({
            visible_1: false,
        })
    }

    handleCancel_1 =e=> {
        console.log(e);
        this.setState({
            visible_1: false,
        })
    }
    
    shouldComponentUpdate(props) {
        return true
    }

    //현재 페이지 내에서 파라미터만 변경되었을 경우
    componentWillReceiveProps(newProps){
        if (this.props.match.params !== newProps.match.params){
            const { apparatusId } = this.props.match.params;
            this.setState({menu : apparatusId})
        }
        //진짜 리스트 만들기
        var newList = []


        this.setState({realReservation : newList})

        //오늘 날짜에만 리스트
        var list124124

    }


    getUrl = (id) => {
        const url = '/apparatus/' + id;
        return (url)
    }

    render() {
        return (
            <div>
                <br/>
                <center><Title style={{ marginBottom: 35 }}>Apparatus</Title></center>

                <Row >
                    <Col span={8} style={{margin : 10}} >
                        {/* 기기들 목록 */}
                        <List
                            grid={{ gutter: 16, column: 1 }}
                            dataSource={this.state.apparatusList}
                            renderItem={item => (
                                <List.Item>
                                    <Link to={this.getUrl(item.id)}><Button type="primary">{item.name}</Button></Link>
                                </List.Item>
                            )}
                        />
                        <Button type="primary" onClick={this.showModal_1}>
                            기기 추가
                        </Button>
                        <Modal
                            title="Basic Modal"
                            visible_1={this.state.visible_1}
                            onOk={this.handleOk_1}
                            onCancel={this.handleCancel_1}
                        >
                            <p>Some contents...</p>
                            <p>Some contents...</p>
                            <p>Some contents...</p>
                        </Modal>
                    </Col>
                    <Col span={13} >
                        {/* 기기들 상세 창 */}
                        <Card>
                            
                            <Row span={3}>  
                                <Button type="link"><Icon type="arrow-left" /></Button>
                                    11월 23일
                                <Button type="link"><Icon type="arrow-right" /></Button>
                            </Row>
                            <Row span={13}>
                                <Col span={3}> 
                                    08 <br/>
                                    09<br/>
                                    10<br/>
                                    11<br/>
                                    12<br/>
                                    13<br/>
                                    14<br/>
                                    15<br/>
                                    16<br/>
                                    17<br/>
                                    18<br/>
                                    19<br/>
                                    20<br/>
                                    21<br/>
                                    22<br/>
                                </Col>
                                <Col span={15}>
                                    {/*예약자 정보*/}
                                </Col>
                            </Row>
                            <Row span={2}>
                                <Col span={19}>
                                    <Button >
                                        예약하기
                                    </Button>
                                </Col>
                                <Col span={5}>
                                    <Button type="link"> 
                                        X
                                    </Button>
                                </Col>
                            </Row>
                        </Card>
                    </Col>
                </Row>


            </div>
        );
    }
}

export default Apparatus;
