export const getFriendButton = (myUserID, othersUserID, relationShip) => {
    if (myUserID == othersUserID){
        return null;
    } else if (relationShip == 1){
        //이미 친구인 상태
        return (
            <span>
                <Button type="danger" size="small" ghost>친구끊기</Button>
            </span>
        )
    } else if (relationShip == 2){
        //이미 친구요청 보낸  상태
        return (
            <span>
                <Button  size="small" disabled>이미신청했음</Button>
            </span>
        )
    } else if (relationShip == 3) {
        //이미 친구요청 받은 상태
        return (
            <span>
                <Button  size="small"  disabled>친구요청받음</Button>
            </span>
        )    
    } else {
        return (
            <span>
                <Button type="primary" size="small" onClick={this.sendRequest} ghost>친구신청</Button>
            </span>
        )    
    }
}

export const loadingRelationship = (myUserID, othersUserID) => {
    const requesturl = 'http://localhost:8080/friend/request/check/' + myUserID + '/' + othersUserID
    fetch(requesturl, {
        method: 'GET',
        headers: {
            'token': getToken()
        }
    })
        .then(response => {
            if (response.status ===200){
                return response.json();
            } else {
                console.log('관계 불러오기 에러!')
                return [];
            }
        }).then(response => this.setState({relationship : response}))
}
