import React, { Component } from 'react';
import { Modal, Button } from 'antd';

import ChemicalInfo from './ChemicalInfo';
import StockInfo from './StockInfo';

//화학 약품 하나에 대한 거 (my Lab 화면 중에)
class ChemicalAdd extends Component {

    state = {
        visible: false,
        information : '',
        keyword : '' 
    }

    constructor(props) {
        super(props);
    }

    showModal = () => {
        this.setState({
            visible: true,
        });
    };

    handleOk = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };

    handleCancel = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };

    handleChange = (e) => {
        this.setState({
          keyword: e.target.value,
        });
      }

    render() {
        const { information, keyword } = this.state;
        const filteredList = information.filter(
          info => info.name.indexOf(keyword) !== -1
        );
        return (
            <span>
                <center>
                    {/* 버튼 */}
                    <Button Button shape="circle" icon="fire" onClick={this.showModal} style={{ fontSize: '25px' }} size="large" />
                    {/* 뜨는 창 */}
                    <Modal
                        title="약품 추가"
                        visible={this.state.visible}
                        onOk={this.handleOk}
                        onCancel={this.handleCancel}
                    >
                        {/* 정보 출력  */}
                        <input
                            placeholder="검색 할 이름을 입력하세요.."
                            onChange={this.handleChange}
                            value={keyword}
                        /> <br />
                        검색된거 정보 <br />
                        <ChemicalInfo chemical={{ chemicalName: "added chemical" }} />
                        별칭 설정 <br />
                        장소 선택 <br />
                        등등 버튼 추가하기
                    </Modal>
                </center>
                <center>
                    약품 추가하기
                </center>
            </span>
        );
    }
}

export default ChemicalAdd;
